INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (26004, 19984, 'Sodium glycerophosphate', 'Renal Insufficiency', 'Sodium glycerophosphate is contraindicated in patients with severe renal insufficiency.  This agent should be used with caution in patients with impaired renal function.  It is recommended to monitor the phosphate status of all patients regularly.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (26005, 6705, 'Sodium citrate', 'Water-Electrolyte Imbalance', 'Alkalinizing agents containing sodium can induce sodium and water retention and result in hypernatremia, hypokalemia, hyperosmolality, edema and aggravation of congestive heart failure.  Therapy with sodium- containing alkalinizing agents should not be used in patients with hypernatremia or fluid retention.  Clinical monitoring of acid/base balance and electrolytes prior to, during, a following completion of therapy with alkalinizing agents is recommended.', '3', '', 'DDInter', 0);
