INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (26094, 1426, 'Sotalol', 'Cerebrovascular Disorders', 'Beta-adrenergic blocking agents (beta-blockers), should be used with caution in patients with cerebrovascular insufficiency because of their potential effects relative to blood pressure and pulse.  If signs or symptoms suggesting reduced cerebral blood flow are observed, consideration should be given to discontinuing these agents.', '2', '', 'DDInter', 0);
