INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (26098, 1426, 'Sotalol', 'Hyperlipidemias', 'Beta-adrenergic receptor blocking agents (aka beta-blockers) may alter serum lipid profiles.  Increases in serum VLDL and LDL cholesterol and triglycerides, as well as decreases in HDL cholesterol, have been reported with some beta-blockers.  Patients with preexisting hyperlipidemia may require closer monitoring during beta-blocker therapy, and adjustments made accordingly in their lipid-lowering regimen.', '2', '', 'DDInter', 0);
