INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (26110, 1426, 'Sotalol', 'Prinzmetal''s variant angina', 'Agents with non-selective beta-blocking activity may provoke chest pain in patients with Prinzmetal''s variant angina.  the use of non-selective beta blockers is not recommended in these patients.  Caution should be taken in the administration of these agents to patients suspected of having Prinzmetal''s variant angina.', '2', '', 'DDInter', 0);
