INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (26301, 23243, 'Solifenacin', 'Central Nervous System Diseases', 'Solifenacin is associated with anticholinergic central nervous system (CNS) effects.  Patients should be monitored for signs of anticholinergic CNS effects, particularly after beginning treatment or increasing the dose.  If a patient experiences anticholinergic CNS effects, dose reduction or drug discontinuation should be considered.  Solifenacin should be used with caution in patients with Parkinson''s disease and in patients with preexisting dementia treated with cholinesterase inhibitors due to the risk of aggravation of symptoms.', '2', '', 'DDInter', 0);
