INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (26401, 25081, 'Spironolactone', 'Kidney Diseases', 'The use of potassium-sparing diuretics is contraindicated in patients with anuria, acute or progressive renal insufficiency, or diabetic nephropathy.  Potassium-sparing diuretics can cause hyperkalemia, which may result in life-threatening cardiac arrhythmias.  Patients with impaired renal function may be particularly susceptible to the hyperkalemic effect of these drugs.  Therapy with potassium-sparing diuretics should be administered cautiously in patients with evidence of renal function impairment (BUN > 30 mg/dL or serum creatinine > 1.5 mg/dL).  If these drugs are used, serum potassium levels and renal function should be monitored at regular intervals.  Determination of serum electrolytes is especially important during initiation of therapy, after a dosage adjustment, and during illness that could alter renal function.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (26402, 266, 'Spironolactone', 'Hyperuricemia', 'Potassium-sparing diuretics have been reported to elevate serum uric acid levels.  Therapy with these agents should be administered cautiously in patients with a history of gout.', '2', '', 'DDInter', 0);
