INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (26435, 27440, 'Stavudine', 'Pancreatitis', 'The reverse transcriptase inhibitors, didanosine (ddI), zalcitabine (ddC), stavudine (d4T) and lamivudine (3TC), may cause pancreatitis.  The incidence is generally low but is approximately 7% with ddI, and up to 15% in pediatric patients given 3TC.  Patients with a history of or known risk factors for pancreatitis, such as alcohol abuse or hypertriglyceridemia, should be monitored closely during therapy with these agents.  Therapy should be discontinued at the first signs or symptoms suggestive of pancreatitis (e.g., nausea, vomiting, abdominal pain, hyperamylasemia with dysglycemia, rising triglycerides, decreasing serum calcium), and preferably permanently discontinued if clinical pancreatitis develops.', '3', '', 'DDInter', 0);
