INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (26440, 27440, 'Stavudine', 'Peripheral Nervous System Diseases', 'The nucleoside reverse transcriptase inhibitors, didanosine (ddI), zalcitabine (ddC), and stavudine (d4T), may commonly cause dose-related peripheral neuropathy, particularly in patients with advanced HIV disease.  Usually, the neuropathy resolves slowly following prompt discontinuation of therapy, but it can be irreversible.  These agents should be administered cautiously to patients with a history of neuropathy and avoided in patients with existing polyneuropathy.  Therapy may be reinstituted following resolution of symptoms in patients who have previously experienced neuropathy with these drugs, but reduced dosages are recommended.', '3', '', 'DDInter', 0);
