INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (26558, 1656, 'Sulfadiazine', 'Urethral Obstruction', 'Sulfonamides are excreted and concentrated in the urine.  Therapy with sulfonamides should be administered cautiously in patients with urinary obstruction or retention, since excessive drug accumulation may occur.  These patients may also be at increased risk for sulfonamide crystalluria, which may be associated with renal toxicity such as uro- and nephrolithiasis, nephritis, toxic nephrosis, hematuria, proteinuria, and elevated BUN and creatinine.  A urinary output of at least 1.5 L/day should be maintained during sulfonamide administration.  Renal function tests and urinalysis should be performed weekly, especially during prolonged therapy (> 2 weeks).', '2', '', 'DDInter', 0);
