INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (26590, 5365, 'Sucralfate', 'Kidney Diseases', 'Patients with chronic renal failure or those receiving dialysis have impaired excretion of absorbed aluminum.  Additionally, aluminum does not cross dialysis membranes as it is bound to albumin and transferrin plasma proteins.  Aluminum accumulation and toxicity (aluminum osteodystrophy, osteomalacia, encephalopathy), have been described in patients with renal impairment.  Sucralfate must be used with caution in patients with chronic renal failure.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (26591, 10957, 'Sucralfate', 'Kidney Diseases', 'Patients with chronic renal failure or those receiving dialysis have impaired excretion of absorbed aluminum.  Additionally, aluminum does not cross dialysis membranes as it is bound to albumin and transferrin plasma proteins.  Aluminum accumulation and toxicity (aluminum osteodystrophy, osteomalacia, encephalopathy), have been described in patients with renal impairment.  Sucralfate must be used with caution in patients with chronic renal failure.', '3', '', 'DDInter', 0);
