INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (26633, 6116, 'Sulindac', 'Hypertension', 'Nonsteroidal anti-inflammatory drugs (NSAIDs), including topicals, can lead to new onset of hypertension or worsening of preexisting hypertension, either of which can contribute to the increased incidence of cardiovascular events.  NSAIDs should be used with caution in patients with hypertension.  Blood pressure should be monitored closely during the initiation of NSAID therapy and throughout the course of therapy.', '2', '', 'DDInter', 0);
