INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (26681, 3184, 'Tamsulosin', 'Glaucoma', 'Intraoperative Floppy Iris Syndrome (IFIS) has been observed during cataract and glaucoma surgery in some patients on or previously treated with alpha 1 blockers, including tamsulosin.  The initiation of therapy with tamsulosin in patients for whom cataract or glaucoma surgery is scheduled is not recommended.  The patient''s ophthalmologist should be aware if the patient is receiving treatment with this drug and should be prepared for possible modifications to the surgical technique, such as the utilization of iris hooks, iris dilator rings, or viscoelastic substances.', '3', '', 'DDInter', 0);
