INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (26745, 11196, 'Tacrolimus', 'Diabetes Mellitus', 'Tacrolimus can induce post-transplant insulin-dependent diabetes mellitus (PTDM).  Temporary insulin therapy has been required in some patients, while long-term insulin use has been necessary in others.   Reversible insulin dependence has occurred in kidney and liver transplant patients.  PTDM has occurred in a greater percentage of tacrolimus-treated (20%) versus cyclosporine-treated (4%) kidney transplant patients.   Black and Hispanic patients are at increased risk for PTDM.  Therapy with tacrolimus should be administered cautiously in patients with pre-transplant diabetes mellitus.', '3', '', 'DDInter', 0);
