INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (26761, 11196, 'Tacrolimus', 'Liver Diseases', 'Tacrolimus undergoes significant hepatic metabolism, primarily by CYP450 3A enzymes.  Less than 5% of tacrolimus is eliminated unchanged in the bile and <1% is eliminated unchanged in the urine.  Moderate or severe hepatic dysfunction or impaired post-transplant hepatic function may alter the metabolic and therapeutic activity of tacrolimus and increase the whole blood tacrolimus concentration.  Therapy with tacrolimus should be administered cautiously and dosage adjustments considered in the presence of moderate or severe hepatic impairment.', '3', '', 'DDInter', 0);
