INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (26825, 11196, 'Tacrolimus', 'Hyperkalemia', 'The use of tacrolimus has been associated with hyperkalemia.  Therapy with tacrolimus should be administered cautiously in patients with elevated serum potassium levels.  Close monitoring of potassium levels is recommended.  Careful consideration should be given prior to use of other agents also associated with hyperkalemia (e.g., potassium-sparing diuretics, ACE inhibitors, angiotensin receptor blockers) during tacrolimus therapy.', '2', '', 'DDInter', 0);
