INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (26869, 6400, 'Sumatriptan', 'Liver Diseases', 'The manufacturer considers the use of sumatriptan to be contraindicated in patients with severe hepatic impairment.  Sumatriptan is primarily metabolized by the liver.  Following subcutaneous administration, the pharmacokinetics of sumatriptan were not significantly altered in patients with hepatic impairment compared to healthy controls.  Following oral administration, however, the bioavailability was substantially increased compared to controls, presumably because the liver plays an important role in the presystemic clearance of orally administered sumatriptan.  The maximum single oral dose should generally not exceed 50 mg in patients with impaired hepatic function.  Data are not available for the nasal formulation.', '3', '', 'DDInter', 0);
