INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (26887, 8949, 'Tamoxifen', 'Endometrial Hyperplasia', 'Endometrial changes such as hyperplasia, polyps, and endometrial cancer have been reported during tamoxifen therapy.  Patients should be instructed to immediately report any signs or symptoms of uterine abnormality such as menstrual irregularities, abnormal vaginal bleeding, change in vaginal discharge, or pelvic pain or pressure.  Therapy with tamoxifen should be administered cautiously in patients with or history of gynecological abnormalities.', '2', '', 'DDInter', 0);
