INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (26893, 8949, 'Tamoxifen', 'Liver Diseases', 'Tamoxifen is extensively metabolized by the liver and excreted in the feces.  Alteration in liver enzyme levels have been noted.  Severe hepatic injuries such as fatty liver, cholestasis, hepatitis, and hepatic necrosis are rare, however, deaths have been reported.  Patients should be instructed to immediately report any sign or symptoms of hepatic dysfunction such as jaundice, dark urine, right upper quadrant pain, or anorexia.  Therapy with tamoxifen should be administered cautiously in patients with or predisposed to compromised hepatic function.', '2', '', 'DDInter', 0);
