INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (26899, 8949, 'Tamoxifen', 'Bone Marrow Failure Disorders', 'Hematological abnormalities during tamoxifen therapy may include thrombocytopenia, leukopenia, and anemia.  Rare hemorrhagic episodes, severe neutropenia and pancytopenia have been reported.  Therapy with tamoxifen should be administered cautiously to patients with or predisposed to bone marrow suppression.', '2', '', 'DDInter', 0);
