INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (26955, 4949, 'Sunitinib', 'Thyroid Diseases', 'Thyroid abnormalities, hypothyroidism or hyperthyroidism, have been reported in patients taking sunitinib.  Therapy with sunitinib should be administered cautiously to patients with thyroid dysfunction.  Baseline laboratory measurement of thyroid function is recommended and if appropriate, treatment per standard medical practice should be given prior to initiation of therapy.  Clinical monitoring of thyroid function is recommended during therapy.', '2', '', 'DDInter', 0);
