INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (26959, 4949, 'Sunitinib', 'Kidney Diseases', 'No dosage adjustment of sunitinib is necessary in patients with renal impairment.  However, when sunitinib therapy was given to patients with end-stage renal disease (ESRD) or on hemodialysis, the systemic exposure was 47% lower in subjects with ESRD on hemodialysis compared to subjects with normal renal function.  Therefore, the subsequent doses may be increased gradually up to 2 fold based on safety and tolerability.', '2', '', 'DDInter', 0);
