INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27001, 29867, 'Tegaserod', 'Intestinal Obstruction', 'The use of tegaserod is contraindicated in patients with a history of bowel obstruction, symptomatic gallbladder disease, suspected sphincter of Oddi dysfunction, or abdominal adhesions.  Stimulation of gastrointestinal motility might be harmful in these conditions.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27002, 27986, 'Tegaserod', 'Liver Diseases', 'The use of tegaserod is contraindicated in patients with moderate or severe hepatic impairment, as it has not been adequately studied for treatment in this population.  In subjects with mild hepatic impairment, mean peak plasma concentration (Cmax) and area under the concentration-time curve (AUC) of tegaserod were 16% and 31% higher, respectively, compared to subjects with normal hepatic function.  No dosage adjustment is required in patients with mild hepatic impairment; however, caution is advised when using tegaserod in such patients.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27003, 29867, 'Tegaserod', 'Liver Diseases', 'The use of tegaserod is contraindicated in patients with moderate or severe hepatic impairment, as it has not been adequately studied for treatment in this population.  In subjects with mild hepatic impairment, mean peak plasma concentration (Cmax) and area under the concentration-time curve (AUC) of tegaserod were 16% and 31% higher, respectively, compared to subjects with normal hepatic function.  No dosage adjustment is required in patients with mild hepatic impairment; however, caution is advised when using tegaserod in such patients.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27004, 27986, 'Tegaserod', 'Kidney Diseases', 'The use of tegaserod is contraindicated in patients with severely impaired renal function.  According to the manufacturer, no change in the pharmacokinetics of tegaserod was observed in subjects with severe renal impairment requiring hemodialysis (CrCl < 15 mL/min/1.73 m2).  However, the peak plasma concentration (Cmax) and area under the concentration-time curve (AUC) of the main pharmacologically inactive metabolite of tegaserod increased 2- and 10-fold, respectively, compared to healthy controls.  The clinical significance of this observation is unknown.  No dosage adjustment is required in patients with mild to moderate renal impairment.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27005, 29867, 'Tegaserod', 'Kidney Diseases', 'The use of tegaserod is contraindicated in patients with severely impaired renal function.  According to the manufacturer, no change in the pharmacokinetics of tegaserod was observed in subjects with severe renal impairment requiring hemodialysis (CrCl < 15 mL/min/1.73 m2).  However, the peak plasma concentration (Cmax) and area under the concentration-time curve (AUC) of the main pharmacologically inactive metabolite of tegaserod increased 2- and 10-fold, respectively, compared to healthy controls.  The clinical significance of this observation is unknown.  No dosage adjustment is required in patients with mild to moderate renal impairment.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27006, 27986, 'Tegaserod', 'Diarrhea', 'Tegaserod should not be initiated in patients who are experiencing or frequently experience diarrhea.  Tegaserod stimulates gastrointestinal motility and may cause diarrhea, usually within the first week of treatment.  Patients should contact their physician if they experience severe diarrhea during therapy, or if the diarrhea is accompanied by severe cramping, abdominal pain, or dizziness.', '2', '', 'DDInter', 0);
