INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27017, 6186, 'Bempedoic acid', 'Tendon Injuries', 'Bempedoic acid has been associated with an increased risk of tendon rupture or injury.  Ruptures involving the rotator cuff, biceps, or Achilles tendon have been reported.  Risk increases in patients over 60, those on corticosteroid or fluoroquinolone treatment, patients with renal failure, and patients with previous tendon disorders.  Consider an alternative therapy in patients with a history of tendon disorders or rupture.  Discontinue treatment if rupture of a tendon occurs or consider discontinuation if the patient experiences joint pain, swelling, or inflammation.', '2', '', 'DDInter', 0);
