INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27168, 233, 'Terbutaline', 'Diabetes Mellitus', 'Adrenergic bronchodilators may cause increases in blood glucose concentrations.  These effects are usually transient and slight, but may be significant with dosages higher than those normally recommended.  Large doses of IV albuterol (not commercially available in the U.S.) and terbutaline sulfate have been reported to cause exacerbation of preexisting diabetes mellitus and ketoacidosis.  Therapy with adrenergic bronchodilators should be administered cautiously in patients with diabetes mellitus.  Closer monitoring of blood glucose concentrations may be appropriate.  Systemic adverse effects are minimized, but not abolished, by administration of these agents via oral inhalation.', '2', '', 'DDInter', 0);
