INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27301, 28087, 'Tetracycline', 'Esophageal Diseases', 'The use of oral tetracycline capsules and tablets has been associated with esophageal irritation and ulceration in patients who ingested the drug without sufficient fluid shortly before bedtime.  Therapy with solid formulations of tetracyclines should preferably be avoided in patients with esophageal obstruction, compression or dyskinesia.  If the drugs are used, patients should be advised not to take the medication just before retiring and to drink fluids liberally.', '2', '', 'DDInter', 0);
