INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27303, 7196, 'Testosterone', 'Prostatic Neoplasms', 'The use of androgenic anabolic steroids is contraindicated for male patients with carcinoma of the breast or prostate.  Circulating androgens can be converted in peripheral tissues to estrogens and dihydrotestosterone, which may act as promoters of tumor growth in the breast and prostate, respectively.  Likewise, androgenic agents may cause enlargement of the prostate and should be used cautiously in patients with or predisposed to prostatic hypertrophy.', '3', '', 'DDInter', 0);
