INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27583, 23204, 'Thalidomide', 'Neutropenia', 'The use of thalidomide has been associated with decreased white blood cells counts, including neutropenia.  Therapy with thalidomide should be administered cautiously in patients with preexisting neutropenia.  Treatment should not be initiated if absolute neutrophil count is below 750/mm3.  Monitoring white blood cell count and differential routinely is recommended during treatment.', '2', '', 'DDInter', 0);
