INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27593, 11105, 'Tetracosactide', 'Water-Electrolyte Imbalance', 'Adrenocorticotropic agents may cause hypernatremia, hypokalemia, hypocalcemia, and fluid retention.  Therapy with these agents, particularly if intended for longer than brief periods, should be administered cautiously in patients with preexisting electrolyte disturbances.  Caution is also advised when treating patients with seizure disorders, since electrolyte disturbances may trigger seizure activity.', '3', '', 'DDInter', 0);
