INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27595, 11105, 'Tetracosactide', 'Peptic Ulcer Perforation', 'Adrenocorticotropic agents may cause gastrointestinal perforation and hemorrhage, usually when given in high dosages or for prolonged periods.  They may also mask symptoms of complications such as peritonitis or intraabdominal sepsis.  Therapy with adrenocorticotropic agents should be administered cautiously in patients with diverticulitis, nonspecific ulcerative colitis (if there is a probability of impending perforation, abscess, or other pyogenic infection), or recent intestinal anastomoses.', '3', '', 'DDInter', 0);
