INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27597, 11105, 'Tetracosactide', 'Adrenocortical Hyperfunction', 'The use of adrenocorticotropic agents is contraindicated in patients with adrenocortical hyperfunction.  These agents stimulate the release of cortisol and aldosterone and may aggravate symptoms of hyperadrenocorticism.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27598, 13507, 'Tetracosactide', 'Adrenocortical Hyperfunction', 'The use of adrenocorticotropic agents is contraindicated in patients with adrenocortical hyperfunction.  These agents stimulate the release of cortisol and aldosterone and may aggravate symptoms of hyperadrenocorticism.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27599, 11105, 'Tetracosactide', 'Infections', 'The immunosuppressant and anti-inflammatory effects of adrenocorticotropic agents, particularly in higher dosages, may decrease host resistance to infectious agents, decrease the ability to localize infections, and mask the symptoms of infection.  Secondary infections may be more likely to develop.  In general, adrenocorticotropic agents should not be used in patients with active infections, especially systemic fungal infections, unless they are medically necessary and effective antimicrobial therapy or other appropriate treatment has been instituted.  For patients who have received prolonged adrenocorticotropic therapy who develop a severe or life-threatening infection, supplemental doses of rapid-acting corticosteroids may be required, since these patients may have hypothalamic-pituitary insufficiency.', '3', '', 'DDInter', 0);
