INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27607, 11105, 'Tetracosactide', 'Scleroderma, Localized', 'The use of adrenocorticotropic agents is contraindicated in patients with scleroderma.  Adrenocorticotropic agents may precipitate renal crisis with malignant hypertension in these patients, possibly via steroid-induced increases in renin substrate and angiotensin II levels and decreases in vasodilator prostaglandin production.  Renal failure may ensue.  If treatment is required for inflammatory myositis or pericarditis, glucocorticoids are preferable because of their more predictable pharmacologic effect.  However, glucocorticoids should also be avoided in the long-term treatment of patients with scleroderma for similar reasons.', '3', '', 'DDInter', 0);
