INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27613, 11105, 'Tetracosactide', 'Diabetes Mellitus', 'Adrenocorticotropic agents may impair glucose tolerance and cause hyperglycemia.  Therapy with these agents should be administered cautiously in patients with diabetes mellitus, glucose intolerance, or a predisposition to hyperglycemia.  Patients with diabetes mellitus should be monitored more closely during adrenocorticotropic therapy, and their antidiabetic regimen adjusted accordingly.', '2', '', 'DDInter', 0);
