INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27615, 11105, 'Tetracosactide', 'Ocular Hypertension', 'Prolonged use of adrenocorticotropic agents may cause elevated intraocular pressure and glaucoma with possible damage to the optic nerves.  Long-term therapy with these agents should be administered cautiously in patients with preexisting glaucoma (particularly open-angle glaucoma) or increased intraocular pressure.', '2', '', 'DDInter', 0);
