INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27619, 11105, 'Tetracosactide', 'Myasthenia Gravis', 'While corticotropin has been used effectively in severe, refractory myasthenia gravis to increase muscle strength, it should nevertheless be administered with extreme caution in such setting.  Patients must be treated in an intensive care unit and receive respiratory support, since muscle strength will markedly decrease initially.  Improvement usually occurs several days after the first or second course of treatment.  Similar responses might be expected of other adrenocorticotropic agents, although their use in myasthenia gravis has not been studied extensively.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27620, 13507, 'Tetracosactide', 'Myasthenia Gravis', 'While corticotropin has been used effectively in severe, refractory myasthenia gravis to increase muscle strength, it should nevertheless be administered with extreme caution in such setting.  Patients must be treated in an intensive care unit and receive respiratory support, since muscle strength will markedly decrease initially.  Improvement usually occurs several days after the first or second course of treatment.  Similar responses might be expected of other adrenocorticotropic agents, although their use in myasthenia gravis has not been studied extensively.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27621, 11105, 'Tetracosactide', 'Muscular Diseases', 'Toxic myopathy may occur with the prolonged use of adrenocorticotropic agents, often in patients with disorders of neuromuscular transmission such as myasthenia gravis or in patients receiving neuromuscular blocking agents.  Steroid myopathy is generalized and sometimes accompanied by respiratory weakness and dyspnea.  In some cases, it has resulted in quadraparesis.  Elevations of creatine kinase may also occur, albeit infrequently.  After withdrawal of ACTH therapy, recovery may be slow and incomplete.  Therapy with adrenocorticotropic agents should be administered cautiously in patients with preexisting myopathy or myoneural disorders, since these conditions may confound the diagnosis of steroid-induced myopathy.  The presence of a normal serum CK level, minimal or no changes of myopathy on EMG, and type 2 muscle fiber atrophy on biopsy are helpful in suggesting steroid-induced weakness.  If steroid myopathy is suspected, a dosage reduction or discontinuation of adrenocorticotropic therapy should be considered.', '2', '', 'DDInter', 0);
