INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27644, 684, 'Theophylline', 'Kidney Diseases', 'The metabolites of theophylline, which are generally undetectable in patients with normal renal function, may accumulate in patients with renal impairment and contribute to the toxicity of theophylline.  In addition, the plasma protein binding of theophylline may be significantly decreased in renal impairment, resulting in elevated free drug concentrations and further increasing the risk of toxicity.  Therapy with theophyllines should be administered cautiously in patients with impaired renal function.  Dosage adjustments and more intensive monitoring of serum theophylline concentrations may be required.', '3', '', 'DDInter', 0);
