INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27820, 11385, 'Thioridazine', 'Neuroleptic Malignant Syndrome', 'The central dopaminergic blocking effects of phenothiazines may precipitate or aggravate a potentially fatal symptom complex known as Neuroleptic Malignant Syndrome (NMS).  NMS is observed most frequently when high-potency neuroleptic agents like haloperidol or fluphenazine are administered intramuscularly but may occur with any agent possessing neuroleptic activity given for any length of time.  Clinical manifestations of NMS include hyperpyrexia, muscle rigidity, altered mental status and autonomic instability (irregular pulse or blood pressure, tachycardia, diaphoresis and cardiac arrhythmias).  Phenothiazine therapy should not be initiated in patients with active NMS and should be immediately discontinued if currently being administered in such patients.  In patients with a history of NMS, introduction or reintroduction of phenothiazines should be carefully considered, since NMS may recur.', '2', '', 'DDInter', 0);
