INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27950, 4704, 'Tinidazole', 'Diseases requiring hemodialysis', 'The clearance of tinidazole is significantly increased during hemodialysis; the half-life is reduced from 12 hours to 4.9 hours.  During a 6-hour hemodialysis session, approximately 43% of the amount present in the body is eliminated.  If this drug is administered on the same day as and prior to hemodialysis, an additional dose of tinidazole equivalent to one-half the recommended dose should be administered after the end of the hemodialysis session.', '2', '', 'DDInter', 0);
