INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (27961, 9104, 'Timolol (ophthalmic)', 'Atrioventricular Block', 'The use of ophthalmic beta-adrenergic receptor blocking agents (aka beta-blockers) is considered by manufacturers to be contraindicated in patients with sinus bradyarrhythmia or heart block greater than the first degree (unless a functioning pacemaker is present).  Topically applied beta-blockers are systemically absorbed, with the potential for producing clinically significant systemic effects even at low or undetectable plasma levels.  In cardiac tissues, beta blockade causes a reduction in inotropic as well as chronotropic activity, which may further depress cardiac function in such patients.', '3', '', 'DDInter', 0);
