INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (28100, 9104, 'Timolol', 'Liver Diseases', 'Timolol is partially metabolized by the liver, and both parent drug and metabolites are eliminated by the kidney.  Patients with renal and/or liver disease may be at greater risk for adverse effects from timolol due to decreased drug clearance.  Therapy with timolol should be administered cautiously in patients with renal and/or liver dysfunction.  Dosage adjustments may be necessary.', '3', '', 'DDInter', 0);
