INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (28128, 9104, 'Timolol', 'Hyperthyroidism', 'When beta-adrenergic receptor blocking agents (aka beta-blockers) are used to alleviate symptoms of hyperthyroidism such as tachycardia, anxiety, tremor and heat intolerance, abrupt withdrawal can exacerbate thyrotoxicosis or precipitate a thyroid storm.  To minimize this risk, cessation of beta-blocker therapy, when necessary, should occur gradually with incrementally reduced dosages over a period of 1 to 2 weeks.  Patients should be advised not to discontinue treatment without first consulting with the physician.  Close monitoring is recommended during and after therapy withdrawal.', '2', '', 'DDInter', 0);
