INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (28135, 9104, 'Timolol', 'Myasthenia Gravis', 'Beta-adrenergic receptor blocking agents (aka beta-blockers) may potentiate muscle weakness consistent with certain myasthenic symptoms such as diplopia, ptosis, and generalized weakness.  Several beta-blockers have been associated rarely with aggravation of muscle weakness in patients with preexisting myasthenia gravis or myasthenic symptoms.  Use cautiously in patients with myasthenia gravis.', '2', '', 'DDInter', 0);
