INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (28180, 28179, 'Ticlopidine', 'Kidney Diseases', 'Ticlopidine is primarily eliminated by the kidney, and there is limited experience in patients with renal impairment.  Elevated serum concentration of ticlopidine and prolonged half-live have been noted in patients with mild and moderate renal impairment.  Prolonged bleeding times have been reported in patients with moderate renal impairment.  Therapy with ticlopidine should be administered cautiously in patients with compromised renal function and dosage modifications may be necessary.', '2', '', 'DDInter', 0);
