INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (28201, 20852, 'Tinzaparin', 'Hypertension', 'Heparin should be used with extreme caution in patients with uncontrolled or severe hypertension as these conditions may predispose the patient to hemorrhage during heparin administration.  Blood coagulation tests (e.g., whole blood clotting time, activated partial thromboplastin time) should be performed at appropriate intervals during full-dose heparin administration.  In addition, periodic platelet counts, hematocrits, and tests for occult blood in stool are recommended during the entire course of heparin therapy.  Clinical monitoring of blood pressure is recommended.', '3', '', 'DDInter', 0);
