INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (28303, 1152, 'Tobramycin', 'Dehydration', 'Adequate hydration is crucial to minimize the risk of oto- and nephrotoxicity associated with the use of aminoglycosides.  Dehydration should preferably be corrected prior to initiation of therapy.  In patients who may be at risk for dehydration, such as those with severe and/or prolonged diarrhea or vomiting, fluid status should be monitored closely.  If signs of renal irritation develop during therapy, hydration should be increased as indicated, accompanied by a reduction in dosage if necessary.  Therapy should be withdrawn if urinary output decreases progressively or azotemia increases.', '3', '', 'DDInter', 0);
