INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (28408, 4982, 'Tofacitinib', 'Hepatitis, Viral, Human', 'Viral reactivation (including cases of herpes virus reactivation [e.g., herpes zoster]) was observed in clinical trials with tofacitinib; postmarketing cases of hepatitis B reactivation have been reported with tofacitinib.  Patients should be screened for viral hepatitis in accordance with clinical guidelines before starting therapy with tofacitinib.  Close monitoring is recommended, and appropriate supportive treatment should be initiated if appropriate.', '2', '', 'DDInter', 0);
