INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (28411, 4982, 'Tofacitinib', 'Pancytopenia', 'Adverse hematologic effects including neutropenia, lymphocytosis, and anemia have been associated with the use of tofacitinib.  It is recommended to avoid starting therapy in patients with absolute lymphocyte counts (ALC) less than 500 cells/mm3, absolute neutrophil counts (ANC) less than 1000 cells/mm3, or hemoglobin levels less than 9 g/dL.  For persistent ANC of 500 to 1000 cells/mm3, hemoglobin levels less than 8 g/dL, or a hemoglobin level drop greater than 2 g/dL, therapy should be interrupted until ANC is greater than 1000 cells/mm3 and hemoglobin values have normalized.  Treatment with tofacitinib is not recommended in patients who develop a confirmed ALC less than 500 cells/mm3 or an ANC less than 500 cells/mm3.  Lymphocyte counts should be monitored at baseline and every 3 months thereafter; neutrophil counts and hemoglobin should be monitored at baseline, after 4 to 8 weeks of therapy, and every 3 months thereafter.  Treatment should be modified based on ALC, ANC, and/or hemoglobin levels.  Caution is recommended in patients who may be at increased risk.', '2', '', 'DDInter', 0);
