INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (28438, 6841, 'Tirofiban', 'Kidney Diseases', 'Tirofiban is primarily eliminated by the kidney.  Approximately 65% of tirofiban is excreted in the urine, largely unchanged.  The serum concentration of tirofiban is significantly increased (>50%) in patients with a creatinine clearance <30 mL/min (including hemodialysis patients).  Therapy with tirofiban should be administered cautiously and initiated at a reduced dosage in patients with severe renal impairment.  Clinical monitoring of renal function and bleeding activity is recommended.  Tirofiban is removed by dialysis.', '3', '', 'DDInter', 0);
