INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (28601, 28069, 'Tolterodine', 'Kidney Diseases', 'The renal elimination of tolterodine and active metabolite is limited and a prolonged half-life is not anticipated in patients with renal impairment.  However, the pharmacokinetic disposition of tolterodine has not been evaluated in patients with renal impairment and it is recommended that tolterodine be administered cautiously in patients with compromised renal function.', '2', '', 'DDInter', 0);
