INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (28723, 3025, 'Topiramate', 'Acidosis', 'Reduced plasma bicarbonate levels and, in some instances, elevated plasma chloride levels may result in metabolic acidosis during long-term therapy with carbonic anhydrase inhibitors.  Therapy with carbonic anhydrase inhibitors should be administered cautiously in patients with metabolic or hyperchloremic acidosis or with conditions that predispose to acidosis (renal disease, severe respiratory disorders, diarrhea).  The measurement of baseline and periodic serum bicarbonate is recommended.  If metabolic acidosis develops (it may be corrected by administration of sodium bicarbonate), and persists, a dose reduction or treatment discontinuation should be considered.', '2', '', 'DDInter', 0);
