INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (28745, 3025, 'Topiramate', 'Glaucoma, Angle-Closure', 'A syndrome consisting of acute myopia associated with secondary angle closure glaucoma has been reported in patients receiving topiramate.  Symptoms include acute onset of visual acuity and/or ocular pain.  It typically occurs within 1 month of treatment initiation and it has been reported in both pediatric and adult patients.  Caution is recommended when prescribing topiramate in patients with elevated intraocular pressure regardless of the etiology.', '2', '', 'DDInter', 0);
